import React, { Component } from 'react';
import "./StockChartBar.css";

import 'react-dates/initialize';
import 'react-dates/lib/css/_datepicker.css';

import { DateRangePicker } from 'react-dates';
import StockBarChart from "./StockBarChart";

class StockChartBar extends Component
{
  constructor(props) {
    super(props);
    this.state = {
      startDate: null,
      endDate: null,
      focusedInput: null,
      value:'barChart'
    };
  }

  onSelectChange = (e) => {
    this.setState({value: e.target.value });
  }

  render() {
    const  value = this.state;

     {console.log(this.props.value)}
    return (

      <div className="main-div">
        <div className="itemcontainer">
          <select className="dropDown" onChange={this.onSelectChange} value={value}>
            <option selected value="barChart">BarChart</option>
            <option value="lineChart">LineChart</option>
          </select>
        <DateRangePicker
          startDateId="startDate"
          endDateId="endDate"
          startDate={this.state.startDate}
          endDate={this.state.endDate}
          onDatesChange={({ startDate, endDate }) => { this.setState({ startDate, endDate })}}
          focusedInput={this.state.focusedInput}
          onFocusChange={(focusedInput) => { this.setState({ focusedInput })}}
        />
        </div>
        <div>
        {this.state.stockPrice == null || this.state.stockPrice.length==0 ? <div className="null_condition"></div> : (this.state.selectedValue=='barChart'? <StockBarChart stockprice={this.state.stockPrice}/>:<div className="null_condition"></div>)}
      </div>
      </div>

    );
  }
}

export default StockChartBar;
