import React, { Component } from "react";
import "./CompanyNews.css";

class CompanyNews extends Component
{
    render() 
    { 
        return (
            <div className="newscontainer">
                <div className="section">
                <header className="header">
                {"News about " + this.props.companyName}
                </header>
                <div  className="scrollable-content newscontent" id="newsContent">
                {
                                this.props.news.map(item=>(
                                    ((item.datetime==null || item.source==null || item.headline==null || item.summary==null)?
                                    <div className="null"></div>:
                                    <div key={item.date} className="content">
                                        <p className="headline">{item.headline}</p>
                                        <p>{"Date : "+item.datetime+'&nbsp &nbsp'+"Source:"+item.source}</p>
                                        <p>{item.summary}</p>
                                        <hr/>
                                    </div>)
                                    ))
                            }
                </div>
                </div>
           </div>
        );
    }
}

export default CompanyNews;
