import React,{Component} from 'react';
// import { Chart } from "react-google-charts";
import { Row, Col, Container } from 'react-bootstrap';
import ApexCharts from 'apexcharts';

class StockCandleStickChart extends Component
{
    render()
    {
        var stockdata=[];
        var title=[];
        title.push('day','low','high','open','close');
        stockdata.push(title);
        this.props.stockprice.map(item=>
            {
                var stockdataArr = [];
                stockdataArr.push(item.date,item.low,item.high,item.open,item.close);
                stockdata.push(stockdataArr);
            })
        console.log(stockdata);    
        return (
            <Container className="chart_container">
            <Row>
                  <Col xs={12} className="chart">
                    <Chart
                      className="barChart"
                    width={'100%'}
                    height={350}
                    chartType="CandlestickChart"
                    loader={<div>Loading Chart</div>}
                    data={stockdata}
                    options={{
                        legend: 'none',
                    }}
                    rootProps={{ 'data-testid': '1' }}
                    />
                    </Col>
            </Row>
       </Container>
        )
    }

}

export default StockCandleStickChart;
