import React, { Component } from "react";
import "./CompanyNews.css";

class CompanyNews extends Component
{

    getnews()
    {
        var getnews=[];
        this.props.news.map(item=>{
            getnews.push(item);
        })
        var results=document.getElementById("news");
        var h1 = document.createElement('h1');
        h1.innerHTML = "News about "+this.props.companyName;
        results.appendChild(h1);
        for(let i=0;i<getnews.length-1;i++)
        {
            var p = document.createElement('p');
            p.innerHTML = getnews[i].source;
            var hr = document.createElement('hr');
            results.appendChild(p);
            results.appendChild(hr);
        }

    }

    render() 
    { 
        return (
            <div className="news_container" id="news">
                {this.getnews()}
           </div>
        );
    }
}

export default CompanyNews;
