import React,{Component} from 'react'; 
import { Row, Col, Container } from 'react-bootstrap';
import ReactApexChart from "react-apexcharts";
import "./StockBarChart.css"

class TimeChart extends Component {
    constructor(props) {
      super(props);

      this.state = {

        series: [{
            name: "Desktops",
            data: [10, 41, 35, 51, 49, 62, 69, 91, 148]
        }],
        options: {
          chart: {
            height: 350,
            type: 'line',
            zoom: {
              enabled: false
            }
          },
          dataLabels: {
            enabled: false
          },
          stroke: {
            curve: 'straight'
          },
          title: {
            text: 'Product Trends by Month',
            align: 'left'
          },
          grid: {
            row: {
              colors: ['#f3f3f3', 'transparent'], // takes an array which will be repeated on columns
              opacity: 0.5
            },
          },
          xaxis: {
            categories: ['05-01-2020 10:00', '05-01-2020 11:00','05-01-2020 12:00','05-01-2020 13:00','05-01-2020 14:00','05-01-2020 15:00','05-01-2020 16:00','05-01-2020 17:00','05-01-2020 18:00','05-01-2020 19:00'],
            labels: {
                datetimeFormatter: {
                  hour: 'HH:mm'
                }
              }
          }
        },


      };
    }



    render() {
      return (
        <Container className="chart_container">
        <Row>
              <Col xs={12} className="chart">
                    <ReactApexChart options={this.state.options} series={this.state.series} type="line" height={350} />
            </Col>
            </Row>
       </Container>

      );
    }
  }

  export default TimeChart;
